<?php  if ( ! defined('BASEPATH')) exit('No direct script access allowed');if ( ! function_exists('css_url')){	function css_url($nom)	{		return base_url() . 'assets/css/' . $nom . '.css';	}}if ( ! function_exists('js_url')){	function js_url($nom)	{		return base_url() . 'assets/js/' . $nom . '.js';	}}if ( ! function_exists('img_url')){	function img_url($nom)	{		return base_url() . 'assets/images/' . $nom;	}}if ( ! function_exists('logo_url')){	function logo_url($nom)	{		return base_url() . 'assets/images/logos/' . $nom . '.jpg';	}}if ( ! function_exists('visuel_url')){	function visuel_url($nom)	{		return base_url() . 'assets/images/visuels/' . $nom . '.jpg';	}}if ( ! function_exists('plugin_url')){	function plugin_url($nom='')	{		return base_url() . 'assets/plugins/' . $nom;	}}//Helper Function for Uploads URLif ( ! function_exists('uploads_url')){	function uploads_url($nom='')	{		return base_url() . 'assets/uploads/' . $nom;	}}//helper function for swf urlsif ( ! function_exists('swf_url')){	function swf_url($number,$color)	{                   //we create the url            return base_url() . 'assets/banner/template'.$number.'/template'.$number.'_'.$color.'.swf';			}}if ( ! function_exists('banner_url')){	function banner_url($number,$color,$id_banner)	{                   //we create the url            return base_url() . 'assets/banner/template'.$number.'/template'.$number.'_'.$color.'.swf?ref='.$id_banner;			}}